export interface ExpenseRecord {
  id: number;
  date: string;
  description: string;
  amount: number;
  category: string;
  paidBy: string;
  bill: string;
  notes: string;
}

export interface AdvanceRecord {
  id: number;
  date: string;
  person: string;
  amount: number;
  notes?: string;
  type: 'regular' | 'returnable';
}

export interface HRPayment {
  id: number;
  date: string;
  month: string;
  amount: number;
  notes?: string;
}

export interface MonthlySummary {
  month: string;
  totalExpenses: number;
  totalAdvances: number;
  balance: number;
  hrPaid: number;
  carryForward: number;
  status: 'ADVANCE LEFT' | 'EXPENSE EXCEEDED' | 'BALANCED' | 'CLEARED BY HR';
}

export interface CategorySummary {
  category: string;
  amount: number;
  percentage: number;
  count: number;
}

